INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 15L, 'Allgemeinwissenschaftliche Wahlpflichtfächer', 'https://zpa.cs.hm.edu/public/module/15/', 'Prof. Dr. Edda Eich-Soellner', '2', '2', 'Deutsch, Englisch', 2, 'je nach Fach', 'in jedem Semester', 'Eine Übersicht über die wählbare Fächer findet sich im Vorlesungsverzeichnis der Fakultät 13. Für das WiSE 22/23:  https://w3-mediapool.hm.edu/mediapool/media/fk13/fk13_lokal/pdfaw/vorlesungverzeichnisss_11_teil_ii/AW_Vorlesungsverzeichnis_WiSe2022_23.pdf 
 Es sind im Studium 2 AW-Fächer mit jeweils mindestens 2 ECTS zu wählen, so dass sich in Summe 4 ECTS ergeben. 
