INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 45L, 'Computergrafik und Bildverarbeitung', 'https://zpa.cs.hm.edu/public/module/45/', 'Prof. Dr. Alfred Nischwitz', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'in jedem Semester', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Lineare Algebra, C++-Programmierkenntnisse', 'Die Studierenden besitzen die Kompetenz: 
 
