INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 67L, 'Digitale Bildverarbeitung', 'https://zpa.cs.hm.edu/public/module/67/', 'Prof. Dr. Claudius Schnörr', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'in jedem Wintersemester', 'Präsenzstudium: ca. 40h, Eigenstudium: ca. 110h', 'Lineare Algebra (IF-I-B-103), Computergrafik und Bildverarbeitung (IF-I-B-601) 
 Nützlich: Systemtheorie (IF-I-M302), Integraltransformationen (IF-I-B-303)', 'Fachkompetenz:  
 
