INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 69L, 'Diskrete Mathematik', 'https://zpa.cs.hm.edu/public/module/69/', 'Prof. Dr. Edda Eich-Soellner', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Übung', 'in jedem Sommersemester', '40 Präsenzstunden Vorlesung, 20 Präsenzstunden Übung, 3 Stunden Arbeit am JiTT-Material, 37 Stunden Vor-/Nachbereitung der Übungen, 50 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Inhaltliche Voraussetzungen : 
 
