INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 79L, 'Enterprise Information Management', 'https://zpa.cs.hm.edu/public/module/79/', 'Prof. Dr. Peter Mandl', '4', '5', 'Deutsch', 1, 'SU mit Übung', 'nach Ankündigung', 'Präsenzstudium: ca. 42 Std., Eigenstudium: ca. 108 Std.', 'Grundlagen der Informatik und Mathematik etwa aus den Modulen Wirtschaftsinformatik und Statistik & Operations Research 
