INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 91L, 'Financial Engineering', 'https://zpa.cs.hm.edu/public/module/91/', 'Prof. Dr. Silja Grawert', '4', '5', 'Deutsch', 1, 'SU mit Übung', 'in jedem Wintersemester', '40 Präsenzstunden Vorlesung, 20 Präsenzstunden Übung, 35 Stunden Vor-/Nachbereitung der Übungen, 55 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Kenntnisse in Finanzprodukten und Finanzmärkten (z.B. IF-S-M-101) sowie in Maß- und Wahrscheinlichkeitstheorie (z.B. IF-S-M-103),', 'Verständnis grundlegender Vorgehensweisen bei der Konstruktion und Analyse von maßgeschneiderten Finanzierungs- und Investitionsinstrumenten unter Verwendung von der in der Praxis gängigen Modellierungsmethoden; Einübung in die Denkweise und Analyse und Modellierungstechniken der Finanzmathematik; Schärfung der Präsentations- und Kommunikationsfähigkeiten', 'Behandlung der verschiedenen Arten von Finanzmarktinstrumenten und gängigen Modellen zur Risikobewertung und zum Pricing; insbesondere Bewertung von Futures, Swaps und Optionen; No-Arbitrage Pricing, Martingalmethoden und Monte-Carlo-Simulationstechniken; Einführung in Zinsstrukturmodelle; Behandlung von strukturierten Produkten und Kreditderivaten;Erläuterungen zum Marktumfeld', 'Tafel, Folien oder Beamer', 'Capinski, Zastawniak: Mathematics for Finance: An Introduction to Financial Engineering 
