INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 95L, 'Fortgeschrittene numerische Mathematik', 'https://zpa.cs.hm.edu/public/module/95/', 'Prof. Dr. Edda Eich-Soellner', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'im Wechsel mit anderen Fächern der gleichen Fachgruppe', '40 Präsenzstunden Vorlesung, 20 Präsenzstunden Übung, 35 Stunden Vor-/Nachbereitung der Übungen, 55 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Numerische Mathematik (IF-I-B-M02)', 'Die Studierenden sind in der Lage, 
 
