INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 96L, 'Fundamentals of Communication Security', 'https://zpa.cs.hm.edu/public/module/96/', 'Prof. Dr. Gudrun Socher', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Übung', 'im Wechsel mit anderen Fächern der gleichen Fachgruppe', '40 Präsenzstunden Vorlesung, 20 Präsenzstunden Praktikum, 35 Stunden Vor-/Nachbereitung der Praktika, 55 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'no prerequisites', 'Upon successful completion of the course, the student will have the knowledge and ability to ensure communication security using modern cryptographic techniques to design new secure services of Internet applications.', 'Course Outline: 
 
