INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 102L, 'GPGPU - General Purpose Programmierung auf Grafikkarten', 'https://zpa.cs.hm.edu/public/module/102/', 'Prof. Dr. Max Fischer', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'nach Ankündigung', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Programmierkenntnisse', 'Kenntnis der Methoden der Parallelprogrammierung mit Grafikhardware 
 Die Fähigkeit, zu erkennen, welche Problemstellungen mit Methoden der Parallelprogrammierung auf Grafikhardware lösbar sind 
 Die Fähigkeit, geeignete Probleme mit GPGPU-Methoden zu lösen', 'Überblick über die Historie von GPGPU von programmierbaren Shadern bis hin zu aktuellen Methoden und Standards. 
