INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 152L, 'Medizinische Bildverarbeitung', 'https://zpa.cs.hm.edu/public/module/152/', 'Prof. Dr. Claudius Schnörr', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'im Wechsel mit anderen Fächern der gleichen Fachgruppe', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Kenntnisse in Bildverarbeitung und Computergrafik', 'Kenntnis wesentlicher Konzepte und Fähigkeit, selbständig medizinische Bildverarbeitungsaufgaben zu lösen.', 'Die Verarbeitung medizinischer Bilder mittels Computer ist für die Diagnose und Therapie unentbehrlich geworden. Die Vorlesung stellt notwendige Gundlagen bereit und behandelt wesentliche Teilgebiete: 
 
 Bildgebende Verfahren und Systeme 
 Strategien zur Verarbeitung medizinischer Bilder 
 Transformationen für medizinische Bilder 
 Filter für medizinische Bilder 
 Morphologische Operationen 
 Segmentierung medizinischer Bilder 
 Merkmalsextraktion und Merkmalsanalyse 
 Klassifikationsansätze 
 Analyse und Synthese medizinischer Bilder 
 Exemplarische medizinische Fragestellungen aus den Bereichen: Dermatologie, Zytologie und Histologie sowie Computertomographie', 'Beamer, Tafel, Demonstrationen mit einem Bildverarbeitungsprogramm', 'Lehmann T., Oberschelp W., Pelikan E., Repges R.:  Bildverarbeitung für die Medizin . Springer-Verlag, Berlin, 1997 
