INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 168L, 'Numerische Mathematik', 'https://zpa.cs.hm.edu/public/module/168/', 'Prof. Dr. Rainer Fischer', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'in jedem Wintersemester', '40 Präsenzstunden Vorlesung, 20 Präsenzstunden Praktikum, 55 Stunden Vor-/Nachbereitung des Praktikums, 35 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Analysis (IF-I-B-101), Lineare Algebra (IF-I-B-103), Angewandte Mathematik (IF-I-B-202)', 'Die Studierenden sind in der Lage, 
 
 die wichtigsten numerischen Problemstellungen zu identifizieren; 
 geeignete numerische Methoden und Algorithmen auszuwählen, fachgerecht zu implementieren, ihr Konstruktionsprinzip zu verstehen, ihre Grenzen zu kennen, sie sicher anzuwenden und auf spezielle Problemstellungen anzupassen 
 die Ursachen für das Versagen eines Algorithmus zu analysieren und fachgerecht zu beheben;', 'Einführung in den Entwurf und die Analyse von Methoden und Algorithmen zur Lösung mathematischer Probleme in Wissenschaft und Technik: 
 
 Einführung in ein Programmsystem zur Lösung von Aufgaben des wissenschaftlichen Rechnens, z.B. Matlab, Scilab oder Octave; 
 Fehleranalyse, Stabilität von Algorithmen; 
 Lösung linearer Gleichungssysteme (quadratisch und überbestimmt); 
 Lösung nichtlinearer Gleichungssysteme; 
 Interpolation und Approximation; 
 Numerische Differentiation und Integration; 
 
 Im begleitenden Praktikum werden kleine Anwednungsaufgaben in Zweierteams gelöst.', 'Folien bzw. Beamer, Demonstration mit Hilfe eines Programmpaketes zum wissenschaftlichen Rechnen, Tafel, Just in Time Teaching und Peer Instruction', 'Michael T. Heath, Scientific Computing: An Introductory Survey , McGraw-Hill Higher Education, ISBN 978-0071244893 
 Timothy Sauer,  Numerical Analysis, Pearson , ISBN 0-321-46135-5 
 Charles F. Van Loan,  Introduction to Scientific Computing: A Matrix-Vector Approach Using Matlab , Pearson, ISBN 0-13-125444-8 
 Wolfgang Preuß and Günter Wenisch (Hrsg.),  Lehr- und Übungsbuch Numerische Mathematik , Fachbuchverlag Leipzig, ISBN 3-446-21375-9 
 Cleve B. Moler,  Numerical Computing with MATLAB ,Society for Industrial Mathematics, ISBN 978-0898715606');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 169L, 'Numerische Mathematik II', 'https://zpa.cs.hm.edu/public/module/169/', 'Prof. Dr. Rainer Fischer', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'in jedem Sommersemester', '40 Präsenzstunden Vorlesung, 20 Präsenzstunden Praktikum, 50 Stunden Vor-/Nachbereitung des Praktikums, 40 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Inhaltliche Voraussetzung :  
 
 Numerische Mathematik I  
 Mehrdimensionale Differentialrechnung Und Differentialgleichungen', 'Die Studierenden sind in der Lage, 
 
 fast alle numerischen Problemstellungen zu identifizieren; 
 numerische Methoden und ihr Konstruktionsprinzip zu verstehen, sicher mit ihnen umzugehen, und das Prinzip auf andere Anwendungen zu übertragen; 
 Algorithmen in Bezug auf Genauigkeit, Komplexität, Effizienz und Stabilität zu untersuchen und zu bewerten 
 geeignete Verfahren auszuwählen, effizient zu implementieren,  ihre Grenzen zu kennen und auf spezielle Problemstellungen anzupassen (Strukturausnutzung,...); 
 unter Verwendung von Programmsystemen auch komplexere Probleme kreativ und fachgerecht zu lösen, die numerischen Algorithmen effizient zu implementieren und Rechenergebnisse kritisch zu beurteilen 
 die Ursachen für das Versagen eines Algorithmus zu analysieren und fachgerecht zu beheben 
 
 Im begleitenden Praktikum werden kleine Anwendungsaufgaben gelöst.', 'Numerische Verfahren für 
 
 Gewöhnliche Differentialgleichungen (Anfangs- und Randwertprobleme) 
 Iterative Lösung linearer Gleichungssysteme 
 Optimierung 
 Eigenwertprobleme 
 Trigonometrische Interpolation uns diskrete Fourier-Transformation', 'Folien und Beamer, pdf-Folien für die Studierenden 
 Jupyter-Notebooks 
 Peer Instruction (PI) 
 Studienarbeiten', 'Michael T. Heath,  Scientific Computing: An Introductory Survey , Mcgraw-Hill Higher Education, ISBN 978-0071244893 
 Timothy Sauer,  Numerical Analysis, Pearson , ISBN 0-321-46135-5 
 Wolfgang Preuß and Günter Wenisch (Hrsg.),  Lehr- und Übungsbuch Numerische Mathematik , Fachbuchverlag Leipzig, ISBN 3-446-21375-9 
 Cleve B. Moler,  Numerical Computing with MATLAB , Society for Industrial Mathematics, ISBN 978-0898715606 
 Charles F. Van Loan,  Introduction to Scientific Computing: A Matrix-Vector Approach Using Matlab , Pearson, ISBN 0-13-125444-8');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 170L, 'Objektorientierter Softwareentwurf', 'https://zpa.cs.hm.edu/public/module/170/', 'Prof. Dr. Reinhard Schiedermeier', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'im Wechsel mit anderen Fächern der gleichen Fachgruppe', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Softwareentwicklung I und II', 'Die Teilnehmer sollen in der Lage sein, auf der Basis der Kenntnisse aus den Grundlagenvorlesungen Softwareentwicklung I und II systematisch größere skalierbare und langfristig stabile Software zu entwickeln.', 'Die folgenden Themen werden in der Vorlesung behandelt beziehungsweise im Praktikum an Hand wechselnder Beispiele bearbeitet: 
 
 OO-Entwurfsprinzipien (SOLID): Single Responsibility, Open/closed Principle, Liskov''sches Substitutionsprinzip, Interface Segregation, Dependency Inversion 
 Faustregeln des OO-Entwurfs und deren Konsequenzen (DRY, KISS, YAGNI, Law of Demeter, God classes etc.) 
 Entwurfsmuster zur Trennung von Zuständigkeiten ( GRASP ) 
 Methoden des Refactoring 
 Negative Sourcecode-Merkmale ( Code Smells ), Antipatterns und Umgang damit (Eliminierungsverfahren) 
 Statische Codemetriken (Komplexitäts- und Kopplungsmaße) und deren Kontrolle 
 Selbstanalyse von Code (Reflection und Annotationen) 
 Rolle von Typen, Klassen und Interfaces (Type-Annotations, Nullability) 
 Konzepte Immutability, Gleichheit, Identität, Clones und Kopien von Objekten 
 Lebenszeit von Objekten gegenüber Prozessen und Lokalisierung in unterschiedlichen Systemen (persistente Objekte, Serialisierungsverfahren und ihre Eigenschaften, Transport von Objekten im Netzwerk) 
 Prinzipien des API-Designs 
 Praktische Umsetzung der Konzepte am Beispiel konkreter Java-Technologien 
 Werkzeuge zur Unterstützung von Konstruktionsprinzipien (statische Stylechecker, Profiler, Coverage-Tools)', 'Tafel, Beamer, Praktikum', 'Fowler: Refactoring – Improving the Design of Existing Code 
 Martin: Clean Code, A Handbook of Agile Software Craftsmanship 
 GOF: Design Patterns 
 Liskov, Guttag: Program Development in Java – Abstraction, Specification, and Object-Oriented Design 
 Beck: Test Driven Development');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 171L, 'Ökonometrie', 'https://zpa.cs.hm.edu/public/module/171/', 'Prof. Dr. Andreas Zielke', '4', '5', 'Deutsch', 1, 'SU mit Übung', 'nach Ankündigung', '40 Präsenzstunden Vorlesung, 20 Präsenzstunden Übung, 35 Stunden Vor-/Nachbereitung der Übungen, 55 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Lineare Algebra, Analysis und Stochastik auf Bachelor-Niveau', 'Verständnis der grundlegenden Modellansätze in der Ökonometrie und Erwerb der Kompetenz bezüglich ihrer Verwendung zur Lösung von Problemstellungen in Wirtschaft und Finanzen. Die Studierenden entwickeln insbesondere ein Bewusstseins für die verschiedenen möglichen Fehlspezifikationen von ökonometrischen Modellen und sind in der Lage, diese zu erkennen und in ihren eigenen Analysen zu vermeiden.', 'Gegenstand der Ökonometrie, einfache und multiple lineare Regressionsmodelle, Eigenschaften der KQ-Schätzer, Gauß-Markov-Theorem, Bestimmtheitsmaße, Verletzung von Modellannahmen und ihre Konsequenzen, geeignete Kennzahlen, Tests und Neuspezifikationen, Strukturbrüche, Nicht-Linearitäten, Multikollinearität, Heteroskedastizität, Autokokorrelation; dynamische Modelle; Prognosen und Prognosegenauigkeit; 
 Beispiele für das Auftreten der verschiedenen ökonometrischen Modelle in der Praxis mit Einführung des wirtschaftlichen Hintergrundes', 'Tafel, Beamer, Statistikprogramm', 'Literaturliste wird zu Beginn der Veranstaltung bekannt gegeben. Beispiel-Literatur: 
 
 Angrist, Pischke: Mostly Harmless Econometrics 
 Brooks: Introductory Econometrics for Finance 
 Campbell, Lo, MacKinlay: The Econometrics of Financial Markets 
 Davidson, MacKinnon: Econometric Theory and Methods 
 Dreger, Kosfeld, Eckey: Ökonometrie. Grundlagen, Methoden, Beispiele 
 Greene: Econometric Analysis 
 Kennedy: A Guide to Econometrics 
 von Auer: Ökonometrie: Eine Einführung');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 172L, 'Operations Research', 'https://zpa.cs.hm.edu/public/module/172/', 'Prof. Dr. Edda Eich-Soellner', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Übung', 'in jedem Wintersemester', '40 Präsenzstunden Vorlesung, 20 Präsenzstunden Übung, 35 Stunden Vor-/Nachbereitung der Übungen, 55 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Inhaltliche Voraussetzungen : 
 
 Lineare Algebra (IF-I-B-103) 
 Analysis (IF-I-B-101) 
 Angewandte Mathematik (IF-I-B-202) 
 Diskrete Mathematik (IF-I-B-201)', 'Die Studierenden sind in der Lage 
 
 Optimierungsaufgaben zu identifizieren, zu abstrahieren, zu modellieren, zu klassifizieren, 
 Lineare und ganzzahlige Modelle zu formulieren 
 Optimierungsprobleme zu klassifizieren 
 Dualitätstheorie für lineare Programme einzusetzen und duale Programme inhaltlich zu interpretieren. 
 Verschiedene Lösungsmethoden zur Lösung linearer Programme anzuwenden 
 Lineare Probleme unter Berücksichtigung von Datenunsicherheiten zu analysieren und zu lösen 
 Ganzzahlige Optimierungsprobleme mittels geeigneter exakter und heuristischer Verfahren zu lösen und die erhaltenen Lösungen zu interpretieren 
 Methoden der dynamischen Programmierung für zusammenhängende bzw. abhängige Entscheidungen einzusetzen 
 Zur Lösung der jeweiligen Problemstellungen geeignete Software auszuwählen, anzupassen und einzusetzen 
 Lösungen zu interpretieren und kritisch zu analysieren', 'Modellbildung (lineare und nichtlineare Modell); 
 Lineare Optimierung (Geometrie, Simplex, Dualität, Transport- und Zuordnungs-Probleme); 
 Klassische Optimierung (Ein- und mehrdimensional, mit und ohne Nebenbedingungen); 
 Graphentheorie: Kürzeste Wege in Graphen, Minimum Spanning Trees 
 Diskrete Optimierung (Traveling Salesperson, Zuordnungsprobleme, Lösungsverfahren: Branch and Bound, Dynamische Optimierung; Näherungsverfahren); 
 Dynamische Optimierung; 
 Warteschlangentheorie; 
 Optimierung durch Simulation; 
 Netzplantechnik (CPM).', 'Tafel, Folien bzw. Beamer, Just in Time Teaching und Peer Instruction, Demonstrationen mit Software zur Lösung linearer und nichtlinearer Optimierungsaufgaben', 'Wolfgang Domschke and Andreas Drexl:  Einführung in Operations Research , Springer, ISBN 978-3540709480 
 Wolfgang Domschke and Andreas Drexl and Robert Klein and Armin Scholl and Stefan Voß,  Übungen und Fallbeispiele zum Operations Research , Springer, ISBN 978-3540716648 
 H.A. Eiselt, Carl-Louis Sandblom,  Operations Research , Springer, ISBN 978-3-642-31053-9');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 173L, 'Optimierung', 'https://zpa.cs.hm.edu/public/module/173/', 'Prof. Dr. Andreas Zielke', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'in jedem Wintersemester', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Analysis(IF-I-B-101), Lineare Algebra(IF-I-B-103), Numerische Mathematik(IF-I-B-M02)', 'Fundierte Kenntnisse der Theorie und Praxis grundlegender Konzepte der Optimierung, insbesondere der Modellierung von Optimierungsproblemen und der Dualitätstheorie. 
 Fähigkeit, mit diesen Methoden Optimierungsprobleme aus verschiedenen Anwendungsgebieten zu formulieren, zu klassifizieren und mit geeigneten Paketen zu lösen.', 'Es werden Anwendungen der linearen und nichtlinearen (konvexen und nicht-konvexen) Optimierung besprochen. Darüberhinaus werden globale, dynamische sowie stochastische Optimierungsprobleme und ihre Anwendungen angesprochen. Ein Schwerpunkt der Anwendungen liegt hierbei auf finanz- und wirtschaftswissenschaftlichen Fragenstellungen. 
 
 Theorie der restringierten und unrestringierten Optimierung, 
 Dualitätstheorie, 
 Modellierung realer Probleme und ihre Lösung, 
 Auswahl geeigneter Verfahren und Tools. 
 
 Dabei werden die Grundlagen hinreichend ausführlich erörtert, bevor die Modellierung im Detail besprochen wird. Die Modellierung und Lösung der Probleme erfolgt in Standardsoftware der Optimierung (Matlab, GAMS, AMPL, Mathematica).', 'Tafel, Folien oder Beamer, Demonstrationen an Hand eines Computeralgebrasystems oder Programmsystems zum wissenschaftlichen Rechnen (z.B. Matlab)', 'Jarre, Stoer: Optimierung, 
 Zenios: Financial Optimization, 
 Zenios: Practical Financial Optimization, 
 Cornuejols, Tutuncu: Optimization Methods in Finance.');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 174L, 'Numerische Optimierung', 'https://zpa.cs.hm.edu/public/module/174/', 'Prof. Dr. Rainer Fischer', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'in jedem Sommersemester', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Analysis(IF-I-B-101), Lineare Algebra(IF-I-B-103), Numerische Mathematik(IF-I-B-M02)', 'Die Studierenden kennen grundlegender Methoden der Optimierung in Theorie und Praxis. 
 Die Studierenden lösen mit diesen Methoden Optimierungsprobleme aus verschiedenen Anwendungsgebieten wie Parameteroptimierung, nichtlineare Regression, Approximation oder optimale Steuerung 
 Die Studierenden wählen zur Lösung von praktischen Problemen geeignete Methoden aus vorhandenen Programmbibliotheken aus', 'Es werden einige der folgenden modernen Verfahren zur Lösung von beschränkten und unbeschränkten, nichtlinearen Optimierungsproblemen besprochen: 
 
 Methode von Nelder-Mead, 
 Gradientenverfahren, 
 CG- und Quasi-Newton-Verfahren, 
 Trust-Region-Verfahren, 
 Innere-Punkte-Methode, 
 ASM und andere Verfahren für quadratische Optimierungsprobleme, 
 SQP-Methode, 
 Lagrange- und Penalty- und Barriereverfahren. 
 
 Dabei wird die Theorie dieser Methoden eingehend erläutert und die Verfahren werden in Computerprogramme umgesetzt. Mit diesen Programmen werden dann konkrete Optimierungsprobleme gelöst.', 'Tafel, Folien oder Beamer, Demonstrationen an Hand eines Computeralgebrasystems oder Programmsystems zum wissenschaftlichen Rechnen (z.B. Matlab)', 'J. Nocedal, ST. Wright:  Numerical Optimization , Springer, ISBN 978-0387987934 
 P. Gill, M. Wright:  Practical Optimization , ISBN 978-0122839528 
 W. Alt:  Nichtlineare Optimierung: Eine Einführung in Theorie und Anwendungen , Vieweg, ISBN 978-3528031930 
 C. Geiger:  Theorie und Numerik restringierter Optimierungsprobleme , Springer, ISBN 978-3540427902 
 Geiger, Kanzow:  Numerische Verfahren zur Lösung unrestringierter Optimierungsaufgaben , Süpringer, ISBN 978-3540662204');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 175L, 'Organisation und Personal', 'https://zpa.cs.hm.edu/public/module/175/', 'Prof. Dr. Jessica Slamka', '4', '5', 'Deutsch', 1, 'SU', 'in jedem Wintersemester', 'Präsenzstudium: ca. 42 Std., Eigenstudium: ca. 108 Std.', 'Grundlagen der Betriebswirtschaft, insbesondere Inhalte der Vorlesungen Betriebswirtschaft aus dem Grundstudium', 'LERNZIELE :
Dieses Modul soll Studierenden einen Einblick in die Organisation als Managementaufgabe und ihre Bedeutung für die betrieblichen Informations- und Entscheidungsprozesse vermitteln. 
 FACH- & METHODENKOMPETENZ :
Die Studierenden sollen Fertigkeiten im Umgang mit den wesentlichen Organisationsmethoden und Organisationstechniken erwerben. Sie sollen Kenntnisse zur Bedeutung der menschlichen Arbeit, der Mitarbeiterführung sowie des Personalwesens für das moderne Unternehmen und einen Überblick über das personalwirtschaftliche Instrumentarium erhalten. 
 ÜBERFACHLICHE KOMPETENZ :  
 
 Teamarbeit: Die Studierenden erarbeiten sich Teilgebiete in eigenständig und Kleingruppen. 
 Fallstudien: Die Studierenden bearbeiten Fallbeispiele an der Schnittstelle von IT und Organisation in Unternehmen. 
 Die Studierenden erfahren Lerntechniken, wie sie sich ein für sie neues Thema schnell und in ausreichender Breite und Tiefe erschließen können.', 'Überblick: Organisation als Managementaufgabe; theoretische Ansätze. 
 Aufbau- und Ablauforganisation: Ziele; Instrumente; Organisationsprinzipien. 
 Methoden und Techniken der Organisationsgestaltung. 
 Organisation der Informationsverarbeitung. 
 Personalpolitische Aufgaben im Unternehmen. 
 Personalwirtschaftliches Instrumentarium. Personalorganisation, Methoden der Personalplanung, der
Personalbeschaffung und der Personalauswahl, Einarbeitung und Einsatz von Mitarbeitern,
Personalbetreuung, Personalentwicklung und -förderung, Personalfreisetzung. 
 Personalführung: Wichtige psychologische und gruppendynamische Aspekte. Anwendung in der
Führungspraxis, Kommunikation (einschließlich Gesprächsführung), Führungsethik, Motivation. 
 Führungstheorien. Führungsstile, Führungsmodelle, symbolische Führung, systematische Führung,
Optimierung von Anreizsystemen. 
 Personalarbeit: Personalinformationssysteme, Möglichkeit des EDV-Einsatzes im Personalwesen.
Leistungsbewertung und Lohnfindung, Mitbestimmung am Arbeitsplatz. 
 Berufsbilder für Wirtschaftsinformatiker. Einblick in die Eingliederung der Informatik in Betriebe
und typische Berufsbilder der Wirtschaftsinformatik. 
 Verhaltenstheoretische Grundlagen und Psychologie zur Organisation und Personal.', 'Folien (Powerpoint, PDF), Tafel, veranstaltungsspezifische Website in Moodle, allgemeine Informationen (Hinweise im WWW), Bücher und Zeitschriftenartikel', 'Bartscher, Th.; Nissen, R.: Personalmanagement: Grundlagen, Handlungsfelder, Praxis, 2. Auflage, 2017 
 Bleicher, Knut: Organisation, Strategien, Strukturen, Kulturen. 2. Auflage, Gabler, 2012 
 Buchanan, D.; Huczynski, A.: Organizational Behaviour. 9. Auflage, Person, 2016 
 Hofmann, J.: IT-Organisation und -Personal, Vieweg+Teubner, 2010. 
 Kirschten, U.: Nachhaltiges Personalmanagement, Aktuelle Konzepte, Innovationen undUnternehmensentwicklung, 2017 
 Laudon, K.C.,: Wirtschaftsinformatik, 3. Auflagen, Pearson, 2016. 
 Olfert, Klaus: Personalwirtschaft, 16. Auflage, 2015 
 Robbins, Stephen: Essentials of Organizational Behavior 14. Auflage, Pearson, 2017. 
 Scholz, C.: Grundzüge des Personalmanagements. Vahlen, 2. Auflage, München, 2014 
 Schulte-Zurhausen, M.: Organisation., 6. Auflage, Vahlen, München, 2013 
 Stock-Homburg, R.: Personalmanagement: Theorien – Konzepte – Instrumente, 3. Auflage, 2013 
 Vahs, D.: Organisation, 9. Auflage, Schäffer-Poeschel, Stuttgart 2015.');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 176L, 'Photogrammetrische Bildverarbeitung', 'https://zpa.cs.hm.edu/public/module/176/', 'Prof. Dr. Stefan Wallentowitz', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'im Wechsel mit anderen Fächern der gleichen Fachgruppe', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Grundlagen Statistik, Lineare Algebra, Analysis, sichere Programmierkenntnisse in MATLAB', 'Verständnis der Sensorik sowie grundlegender Methoden und Algorithmen der digitalen Photogrammetrie. 
 Fähigkeit, die Methoden und Algorithmen eigenständig in Lösungen mit einer (objektorientierten) Programmiersprache umzusetzen.', 'Die Vorlesung greift moderne Verfahren der photogrammetrischen Computer Vision zur Bildverarbeitung und 3D Rekonstruktion von Objekten auf. Erfahrungen aus aktuellen Forschungsprojekten fließen unmittelbar ein.
Inhalt: 
 
 Bilddatenerfassung 
 Fernerkundungssensoren 
 UAV-basierte Datenerfassung 
 Histogramm Matching, Bildfusion 
 Bildpyramiden 
 Digitale Bildzuordnung 
 3D Objektrekonstruktion mit Dense Matching Methoden 
 Digitale Orthophotos. 3D Punktwolken 
 Mosaiking 
 
 Vorlesungsinhalte werden mit den Daten eines Multisensor-UAV exemplarisch umgesetzt.', 'Tafel, Beamer, selbstgesteuertes Lernen in Kleingruppen, Praktikum in MATLAB', 'Th. Luhmann: Masterkurs Computergrafik und Bildverarbeitung, Vieweg 2004.');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 177L, 'Physik', 'https://zpa.cs.hm.edu/public/module/177/', 'Prof. Dr. Rolf Heilmann', '4', '5', 'Deutsch', 1, 'SU', 'in jedem Semester', 'Siehe Modulkatalog FK08, Studiengang Bachelor Geotelematik und Navigation', 'Siehe Modulkatalog FK08, Studiengang Bachelor Geotelematik und Navigation', 'Siehe Modulkatalog FK08, Studiengang Bachelor Geotelematik und Navigation', 'Siehe Modulkatalog FK08, Studiengang Bachelor Geotelematik und Navigation', 'Siehe Modulkatalog FK08, Studiengang Bachelor Geotelematik und Navigation', 'Siehe Modulkatalog FK08, Studiengang Bachelor Geotelematik und Navigation');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 178L, 'Praktisches Studiensemester', 'https://zpa.cs.hm.edu/public/module/178/', 'Prof. Dr. Georg Peters', '4', '24', 'Deutsch', 1, 'selbständiges Arbeiten', 'in jedem Semester', 'Das gesamte Praktische Studiensemester umfasst 22 Wochen. Es besteht aus den folgenden zwei Komponenten: 
 
 das Praktische Studiensemester (IF-WI-B29), einem Praktikum von 20 Wochen (24 ECTS-Kreditpunkte) in einer
Ausbildungsstelle (Behörde, Unternehmen u. ä.). 
 die Praxisbegleitenden Lehrveranstaltung (IF-WI-B30) im Umfang von 2 Wochen (6 ECTS-Kreditpunkte) mit den beiden Teilen (1) Grundlagen der Wirtschaftsinformatik und (2) Systeme der Wirtschaftsinformatik. 
 
 Diese Modulbeschreibung bezieht sich auf das Praktische Studiensemester (IF-WI-B29), das Praktikum in einer Ausbildungsstelle. Für die Praxisbegleitenden Lehrveranstaltung (IF-WI-B30) sei auf deren Modulbeschreibung verwiesen.', 'Die Voraussetzungen für den Eintritt in das praktische Studiensemester sind in der Studien- und Prüfungsordnung festgelegt.', 'LERNZIELE 
 
 Die Studierenden sind in der Lage, die in den ersten vier Semestern im Bachelor Wirtschaftsinformatik erworbene einschlägige Fach- und Methodenkompetenz in der Ausbildungsstelle (Behörde, Unternehmen u. ä.) anzuwenden, zu vertiefen und weiterzuentwickeln. Aufbauend darauf sind die Studierenden befähigt, den Aufbau und die Abläufe in der Ausbildungsstelle zu verstehen und sich in diesen professionell und erfolgreich zu bewegen. Dieses umfasst das fachspezifische, interdisziplinäre und  administrative Umfeld in der Ausbildungsstelle, das einen einschlägigen, deutlich berufsbezogenen Charakter bietet.  
 
 FACH- & METHODENKOMPETENZ 
 
 Die Studierenden wenden ausgewählte Bereiche der in den vier Semestern im Bachelors Wirtschaftsinformatik erworbenen einschlägigen Fach- und Methodenkompetenz in einer Ausbildungsstelle (Behörde, Unternehmen u. ä.) an. Sie lernen auf Basis ihrer in den vier Semestern im Bachelors Wirtschaftsinformatik erworbenen einschlägigen Fach- und Methodenkompetenz grundlegende, betriebliche Arbeitsweisen im Bereich der Wirtschaftsinformatik kennen und verfestigen und vertiefen diese. 
 
 ÜBERFACHLICHE KOMPETENZ 
 
 Die Studierenden arbeiten in Projekten zu einschlägigen Themen der Wirtschaftsinformatik in (interdisziplinären) Teams zusammen. Sie erarbeiten sich ausgewählte Teilgebiete eines Themenbereiches selbständig und planen ausgesuchte Arbeitsabläufe eigenverantwortlich. Dabei greifen sie auf ihre in den vier Semestern im Bachelors Wirtschaftsinformatik erworbenen Kompetenzen zurück.', 'Inhalte während des Praktikums in der Ausbildungsstelle 
 Mitarbeit in ausgewählten Bereichen der Wirtschaftsinformatik.
Beispielhafte Stichworte: 
 
 Software-Entwicklung und -Engineering (z. B. Analyse, Entwurf, Programmierung, Prüfung) 
 Informationssysteme (ERP-Systeme sowie Schwerpunktthemen wie z.B. BI, CRM oder SCM), Prozessmanagement etc. 
 Data Science, Datenbankmanagement und -administration 
 IT-Infrastrukturen (Cloud Computing etc.); IT-Systemkonzeption, -Realisierung; Verwaltung bzw. Wartung von IT-Systemen 
 IT-Sicherheit und Datenschutz 
 IT-Projektmanagement bzw. -realisierung 
 Betriebswirtschaftliche Bereiche wie beispielsweise Logistik, Marketing, Organisation, Rechnungswesen oder Unternehmensdisposition 
 
 Dabei muss der Inhalt des Praktikums bereits deutlich berufsbezogenen Tätigkeiten gewidmet sein, die auch Absolvent:innen eines Bachelorstudiums der Wirtschaftsinformatik ausüben. 
 Anforderungen an die Ausbildungsstelle (Behörde, Unternehmen u. ä.) 
 
 Bei Unternehmen u. ä.: Jahresumsatz von mindestens EUR 500.00 
 Mindestens fünf unbefristet Beschäftigte in der Ausbildungsstelle 
 Mindestens drei unbefristet Beschäftigte im unmittelbaren einschlägigen Arbeitsumfeld der Praktikant:innen 
 Die Ausbildungsstelle und der Arbeitsplatz der Praktikant:innen befinden sich in einem Bürokomplex, d. h. keine Wohnung u. ä.  
 Im Praktikum können die gleichen betrieblichen Regeln für Telearbeit/mobile Arbeit, die für Vollzeitbeschäftigte im unmittelbaren Arbeitsumfeld gelten, angewendet werden, mit der Beschränkung, dass für Praktikant:innen der Anteil der Telearbeit/mobile Arbeit auf maximal 40% der wöchentlichen Regelarbeitszeit begrenzt ist. Eine darüberhinausgehende Ableistung des Praktikums in Telearbeit/mobile Arbeit (z. B. im Homeoffice) ist im Regelfall ausgeschlossen, sofern Rechtsnormen (staatliche Gesetze und Verordnungen) Telearbeit/mobile Arbeit nicht zwingend fordern. 
 Die Ausbildungsstelle verfügt über eine:n unmittelbare:n Ausbildungsleiter:in oder fachliche:n Betreuer:in für die Praktikant:innen. 
 Praktika in Hochschulinstituten (wie z. B. Fachbereiche, Fakultäten, Forschungsinstitute) u. ä. werden in der Regel nicht anerkannt.', 'Abhängig von der gewählten Ausbildungsstelle.', 'Abhängig von der gewählten Ausbildungsstelle.');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 179L, 'Praktisches Studiensemester IC/DC', 'https://zpa.cs.hm.edu/public/module/179/', 'Prof. Dr. Silja Grawert', '0', '25', 'Deutsch, Englisch', 2, 'selbständiges Arbeiten', 'in jedem Semester', 'Die Praktikumszeit im Unternehmen beträgt für die SPO IC >=2017 22 Wochen, ebenso für die SPOs DC. 
Für ältere SPOs DC 24 Wochen incl. der PBLV.', 'inhaltliche Voraussetzung: Kenntnisse der Informatik und Mathematik entsprechend den Fächern der Semester 1-3. 
 Formale Voraussetzung: 75 ECTS aus den Semestern 1-3', 'Lernziele:  Die Studierenden sollen grundlegende, betriebliche Arbeitsweisen kennenlernen, sich im Bereich der praktischen Tätigkeit orientieren sowie ihre beruflichen Rollen als (Softwareentwickler,
Projektmitarbeiter, usw.) trainieren. 
 Kompetenzen:  Die Studierenden erwerben Erfahrung in der praktischen Tätigkeit in einem Unternehmen.', 'Mitarbeit in ausgewählten Bereichen: 
 
 Softwareentwicklung (z. B. Analyse, Entwurf, Programmierung, Prüfung). 
 Simulation 
 Projektmanagement bzw. -realisierung.', 'Entfällt', 'Entfällt');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 180L, 'Praxisbegleitende Lehrveranstaltung', 'https://zpa.cs.hm.edu/public/module/180/', 'Prof. Dr.-Ing. Martin Hobelsberger', '4', '5', 'Deutsch, Englisch', 2, 'Praktikum', 'in jedem Semester', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung', 'Praxissemester (begleitend oder durchgeführt oder angerechnet)', 'Diese Veranstaltung dient der begleitenden Vermittlung von Wissen und Können zum Praxissemester. Zudem der Vorbereitung auf wissenschaftliches Arbeiten im Rahmen der kommenden Studienabschlußarbeit. Es beinhaltet dabei Teilziele: 
 
 Lernen und Üben gut zu präsentieren 
 Vermittlung von Wissen und Können im Rahmen einer Projektarbeit zu einem ausgewählten Informatik-Thema. Hierbei wird das selbständige Einarbeiten in Fachwissen, das fundierte Konzipieren und Umsetzen von Lösungen, das kreative Handeln, die Fähigkeit zur Kommunikation und Teamarbeit, sowie das Erlernen von Organisationstechniken gefördert und geübt 
 Vermittlung von Aspekten zum Wissenschaftlichen Arbeiten wie Finden eines Themas, Erstellung eines Exposé, Finden eines Prüfers, Bearbeitung und Dokumentation 
 Vermittlung von weiterem geeigneten Wissen (wie z.B. Betriebswirtschaftslehre, Zeitmanagement, o.ä., um wichtige Abläufe in Firmen besser verstehen zu können).', 'Vortrag zum Praxissemester 
 Projektarbeit mit Realisierung eines Projektes aus dem Bereich Informatik, beispielsweise aus dem Bereich der Sicherheit oder Web-Technologien 
 Vermittlung von Aspekten zum Wissenschaftlichen Arbeiten z.B. mittels der Projektdokumentation 
 weiteres geeignetes Wissen, das für das Praxissemester begleitend hilfreich ist (z.B. Wissensvermittlung zu ausgewählten Themen der Betriebswirtschaftslehre mit besonderer Relevanz für Informatiker (wie beispielsweise Firmengründung, Organisation, Marketing) oder Zeitmanagement).', 'Folien/Beamer, Tafel, Flipchart, Bücher/Artikel, kontextspezifische Software', 'Kontextspezifische Literatur');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 181L, 'Praxisbegleitende Lehrveranstaltung - Systeme der Wirtschaftsinformatik', 'https://zpa.cs.hm.edu/public/module/181/', 'Prof. Dr. Georg Peters', '3', '3', 'Deutsch, Englisch', 2, 'Praktikum', 'in jedem Wintersemester', 'Präsenzstudium: ca. 33 Std., Eigenstudium: ca. 0 Std.', 'Grundlagen der Wirtschaftsinformatik, insbesondere folgende Module aus dem Bachelor Wirtschaftsinformatik:: 
 
 Betriebswirtschaft 
 Informationssysteme I und II 
 Statistik und Operations Research 
 Softwareengineering I und II 
 Softwareentwicklung I und II 
 Wirtschaftsinformatik 
 Wirtschaftsmathematik I und II', 'LERNZIELE 
 Die Studierende sollen anhand eines beispielhaften Projektes zu Systemen der Wirtschaftsinformatik ihre Kompetenz in der Projekt- und Teamarbeit vertiefen, um diese in der beruflichen Praxis einzusetzen. Weiterhin sollen die Studierenden eine Plattform haben, sich über ihre Erfahrungen im Praxissemester auszutauschen, um einen unternehmensübergreifenden Einblick in die betriebliche Welt zu erhalten. 
 FACH- & METHODENKOMPETENZ 
 
 Die Studierenden erwerben Kenntnisse über Systeme in der Wirtschaftsinformatik. 
 Die Studierenden erwerben fachtheoretisches Wissen und praktische Erfahrung aus ausgewählten Bereichen der Wirtschaftsinformatik.  
 Die Studierenden erwerben die Fähigkeit, ein Projekt in Teamarbeit abzuwickeln.  
 Sie erwerben praktische Kenntnisse über typische Rollen innerhalb eines Projekts 
 Die Studierenden kennen den grundlegenden Aufbau und Ablauf eines Projektes.  
 Die Studierenden verfügen über die grundlegende Fähigkeit, ein (Teil-)Projekt zu planen und zu steuern. 
 Die Studierenden haben die Fähigkeit, ein Themengebiet der Wirtschaftsinformatik zu analysieren und Lösungen dazu zu erarbeiten optimieren. 
 
 ÜBERFACHLICHE KOMPETENZ 
 
 Die Studierenden arbeiten in Projekten zu Systemen der Wirtschaftsinformatik in Teams zusammen. 
 Die Studierenden erarbeiten sich Teilgebiete des Themenbereiches selbständig und planen ihre Arbeitsabläufe eigenverantwortlich.', 'Projekt zu Systemen der Wirtschaftsinformatik 
 Reflektion und Austausch zu Erfahrungen im Praxissemester', 'Folien (Powerpoint, PDF) und Tafel/Whiteboard 
 Einschlägige Webseiten und Zeitschriftenartikel 
 Labor-PC mit einschlägigen Software, u.a.: 
 ERP Systeme 
 Office Suites 
 Software-Entwicklungsumgebungen', 'Die Literatur zu ausgewählten Themengebieten wird in der Veranstaltung bekanntgegeben.');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 182L, 'Praxisbegleitende Lehrveranstaltung - Grundlagen der Wirtschaftsinformatik', 'https://zpa.cs.hm.edu/public/module/182/', 'Prof. Dr. Patrick Moebert', '3', '3', 'Deutsch', 1, 'Praktikum', 'in jedem Wintersemester', 'Präsenzstudium: ca. 42 Std., Eigenstudium: ca. 0 Std.', 'Gute betriebswirtschaftliche und volkswirtschaftliche Kenntnisse.', 'LERNZIELE 
 Dieses Modul dient der Vermittlung von Kenntnissen bezüglich sozial verantwortlichen Handelns im Unternehmenskontext sowie grundlegender betrieblicher Arbeitsprozessen und Methoden (beispielsweise für Präsentation und Moderation). 
 FACH- & METHODENKOMPETENZEN 
 Die Studierenden erwerben die Fähigkeit, betriebliche Zusammenhänge zu verstehen bzw. zu interpretieren. Darüber hinaus erwerben sie praxisorientierte, grundlegende Schlüsselqualifikationen für das Arbeiten im betrieblichen Umfeld.', 'Erörterung praktischer betrieblicher Zusammenhänge (z. B. Interpretation des Unternehmensgeschehens, Aspekte der Unternehmensorganisation, Bedeutung von Mitarbeiternetzwerken und Hierarchien) bzw. Förderung praxisorientierter Schlüsselqualifikationen, die über das reine Fachwissen hinausgehen (z. B. betriebliche Arbeitstechniken, Ressourcenmanagement, Teamkompetenz).', 'Folien (Powerpoint) und Tafel, Bücher und Zeitschriftenartikel, multimedialen Präsentationen', 'Kontextspezifische Literatur');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 183L, 'Praxisbegleitende Lehrveranstaltung - Unternehmensplanspiel', 'https://zpa.cs.hm.edu/public/module/183/', 'Prof. Dr. Gerhard Stützle', '4', '2', 'Deutsch', 1, 'Praktikum', 'in jedem Wintersemester', 'Präsenzstudium: ca. 42 Std., Eigenstudium: ca. 0 Std.', 'Faktisch, aber nicht formal gute betriebswirtschaftliche und volkswirtschaftliche Kenntnisse', 'Lernziele: Dieses Modul dient der Vermittlung von Kenntnissen der betriebs-wirtschaftlichen Gesamtzusammenhänge. Die Studierenden sollen lernen, volkswirtschaftliche und branchenspezifische Analysen zu erstellen. Sie sollen lernen, wie ihre unternehmerischen Entscheidungen sich in Gewinn- und Verlustrechnung und Bilanz sowie in verschiedenen betriebswirtschaftlichen Kennzahlen niederschlagen. 
 Kompetenzen: Die Studierenden erwerben die Fähigkeit, volkswirtschaftliche und unternehmerische Marktanalysen, eine Unternehmensplanung sowie Entscheidungs- und Kontrollprozesse im Unternehmen durchzuführen. Die Studierenden erwerben die Fähigkeit, betriebliche Zusammenhänge zu verstehen und zu  interpretieren und erwerben praxisorientierte grundlegende Schlüsselqualifikationen wie betriebliche Arbeitstechniken und soziale Kompetenzen.
 Die Studierenden erwerben die Fähigkeit, ein Projekt in Teamarbeit abzuwickeln. Sie erwerben  weiterhin praktische Kenntnisse über typische Rollen innerhalb eines Projekts.', 'Analyse der volkswirtschaftlichen Situation und der branchenspezifischen Marktdaten: Marktformen, unternehmerische Ertragsquellen, Verhaltens- und Reaktionsmechanismen. 
 Planung von Produktion, Kosten, Investitionen, Finanzierung und Absatz eines Unternehmens.
Entscheidungs- und Kontrollprozesse und ihre Auswirkungen auf das Unternehmensergebnis und die Bilanz kennenlernen. 
 Erörterung praktischer betrieblicher Zusammenhänge (z. B. Interpretation des Marktgeschehens, Aspekte der Unternehmensorganisation) bzw. Förderung praxisorientierter Schlüsselqualifikationen,
die über das reine Fachwissen hinausgehen (z.B. betriebliche Arbeitstechniken, Ressourcenmanagement, Teamkompetenz). 
 Projektmanagement, Präsentationstraining, Anfertigen von Projektberichten, Mitarbeiterführung, Ressourcenmanagement.', 'Folien (Powerpoint) und Tafel  Bücher und Zeitschriftenartikel Software (CRM, E-Commerce, u.a.) für Unternehmensanalysen und -entscheidungen  Computersimulationen  multimedialen Präsentationen  kontextabhängig', 'Kontextspezifische Literatur');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 184L, 'Supply Chain Management', 'https://zpa.cs.hm.edu/public/module/184/', 'Prof. Dr. Andre Krischke', '4', '5', 'Deutsch', 1, 'SU', 'in jedem Wintersemester', 'Präsenzstudium: ca. 42 Std., Eigenstudium: ca. 108 Std.', 'Grundlagen der Betriebswirtschaft aus dem Grundstudium', 'LERNZIELE (Welche Kenntnisse vermittelt die Veranstaltung?) 
 Nach dem Besuch dieses Moduls sind die Studierenden in der Lage, die wesentlichen Konzepte und Methoden des Produktions-, Qualitäts- und Prozessmanagements auf praktische betriebswirtschaftliche Fragestellungen anzuwenden. Sie bearbeiten ausgewählte Problemstellungen eigenständig mithilfe entsprechender methodischer Ansätze des Operations und Supply Chain Managements. Die Studierenden tauschen sich in den Übungseinheiten in kleineren Gruppen über die zu behandelnden Fallbeispiele aus der Praxis aus. Durch den Besuch dieses Moduls erkennen die Studierenden u.a. die an welcher Stelle bestimmter Prozesse sich Supply Chain Manager mit anderen Fachbereichen abstimmen müssen und an welchen Stellen des Produktionsmanagements sich in der Praxis wesentliche Optimierungspotentiale realisieren lassen. 
 FACH- & METHODENKOMPETENZ (Was erlernen die Studierenden, was können sie im Anschluss anwenden, analysieren, evaluieren, kreieren?) 
 •   Modelle und Methoden der Analyse (Forschungs- und Analysemodelle): 
Ausgewählte analytische Methoden des Operations und Supply Chain Management, beispielsweise Warteschlangentheorie
•   Quantitativ-Empirische Methoden (Vergleichende – statistische, mathematische Methode, Datenanalysen):  Ausgewählte statistische Methoden des Prozessmanagements, beispielsweise Six Sigma
•   Qualitativ-Interpretative Methoden (Experteninterview, Umfragen, standardisierte Erhebungen): 
Interpretation von Lösungen im Sachkontext und Diskussionen von Fallbeispielen 
 ÜBERFACHLICHE KOMPETENZ (Was erfahren und erlernen die Studierenden über das Fachliche hinaus?) 
 
 Teamarbeit: Die Studierenden erarbeiten sich Teilgebiete eigenständig und in Kleingruppen. 
 Fallstudien: Die Studierenden erarbeiten Fallbeispiele und finden in Kleingruppen zu unternehmerischen Entscheidungen.', '•   Abgrenzung und Gegenstand von Operations und Supply Chain Management
•   Operations & Supply Chain Management Strategie (Direct)
•   Gestaltung Produkt, Prozess und Ressourcen (Design)
•   Systeme der Planung und Steuerung (Deliver)
•   Optimierung und kontinuierliche Verbesserung (Develop)
•   Grundlagen des Prozessmanagement
•   Grundlagen des Supply Chain Managements', 'Moodle, Tafel/ Moderationswand, Whiteboard (via iPad/ Beamer), Präsentationsfolien, kurze Lernvideos (Micro-Learning), Selbstgesteuerte Lernplakate, e-Learning', '•   Slack, Nigel: Operations and Process Management, 5. Auflage, Pearson Education, 2018
•   Abele, Eberhard, Global Production, Springer 2009
•   Friedli et al. Management Globaler Produktionsnetzwerke, Hanser 2013
•   Hopp, Wallace Supply Chain Science, Waveland Pr Inc 2011 
 Weitere Literaturhinweise: siehe Veranstaltungsseite in Moodle');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 185L, 'Programmverifikation', 'https://zpa.cs.hm.edu/public/module/185/', 'Prof. Dr. Matthias Güdemann', '4', '5', 'Deutsch, Englisch', 2, 'SU mit Praktikum', 'im Wechsel mit anderen Fächern der gleichen Fachgruppe', '40 Präsenzstunden Vorlesung, 20 Präsenzstunden Übung, 35 Stunden Vor-/Nachbereitung der Übungen, 55 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Keine', 'Die Studierenden sind in der Lage 
 
 verschiedene formale Verifikationsmethoden zu erläutern 
 formale Verifikationsmethoden auf Beispielprogramme anzuwenden 
 formale Programmspezifikationen auf Basis existierender Programme abzuleiten 
 moderne Werkzeuge zur Programmverifikation einzusetzen 
 zu entscheiden, welche Verifikationsmethodiken für gegebene Aufgaben sinnvoll einsetzbar sind', 'Für Systeme, deren Versagen menschliches Leben, die Gesundheit oder sehr hohe Vermögenswerte gefährdet (sicherheitskritische Systeme), ist das Vertrauen auf erfolgreiche Tests nicht ausreichend. Verifikation ist der mathematische Beweis, dass ein System gewisse für seine Sicherheit wesentliche Eigenschaften besitzt --- vorausgesetzt Programmeigenschaften werden formal spezifiziert.  
 Auf einer gemeinsamen theoretischen Basis werden sowohl die klassische Programmverifikation basierend auf dem Hoare Kalkül als auch abstrakte Interpretation und Model Checking betrachtet. 
 Im Einzelnen 
 
 Grundlagen aus der Logik, Verbände und Fixpunkte 
 Formale Spezifikation von Schnittstellen und Abstrakte Datentypen 
 Axiomatische Verifikation nach Hoare 
 Abstrakte Interpretation nach Cousot 
 Software Model-Checking mittels bitpräziser SMT Solver 
 Anwendung moderner, in der Industrie eingesetzter Verifikationswerkzeuge 
 Begleitung durch Praktikum', 'Tafel, Folien oder Beamer, live Coding, Moodle, Anwendung realer Werkzeuge', 'Apt/Olderog; Verification of Sequential and Concurrent Programs; Springer, 1997 
 Loeckx/Sieber; The Foundations of Program Verification; Teubner; 1987 
 Müller, Peter; Modular Specification and Verification of Object-Oriented Programs; Springer; 2002 
 ISBN 3-540-00296-0 
 Schneider; Verification of Reactive Systems; Springer; 2004; 
 ISBN 0-521-54310 X 
 Huth/Ryan; Logic in Computer Science 
 CACM Computing Surveys, Special Issue on Software Verification, Vol.41 No. 4, 2009 
 Clarke, Grumberg, Kroening, Peled, Veith: Model Checking 2nd Edition, MIT Press, 2018 
 Zusätzlich: Aktuelle Online-Quellen und Dokumentationen');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 186L, 'Projektstudium', 'https://zpa.cs.hm.edu/public/module/186/', 'Prof. Dr. Peter Mandl', '8', '10', 'Deutsch', 1, 'Praktikum', 'in jedem Semester', 'Präsenzstudium: ca. 84 Std., Eigenstudium: ca. 216 Std.', 'Grundlegende Kenntnisse der Wirtschaftsinformatik 
 Basiskenntnisse Projektmanagement z.B. aus dem Bachelorstudium (Modul Wirtschaftsinformatik)', 'LERNZIELE :
Gegenstand der Vermittlung ist das einsatzbezogene Trainieren von Arbeitspraktiken und Techniken der IT-Projektabwicklung anhand eines konkreten, komplexeren Projektes aus dem Bereich Wirtschaftsinformatik, unter realitätsnahen Bedingungen. 
 FACH- & METHODENKOMPETENZ : Die Veranstaltung vermittelt themabezogene folgende Kompetenzen: 
 
 Fähigkeit Ansätze der Softwareentwicklung und des Projektmanagements spezifisch auszuwählen und einzusetzen bzw. diese ggfs. zu adaptieren bzw. weiterzuentwickeln. 
 Methodisch: Fähigkeit, themabezogene Problemstellungen zu analysieren und durch geeignete Methoden, zielführend zu bearbeiten. 
 Interpersonell: Eigene Arbeitsprozesse im praktischen Einsatz zu reflektieren und situationsbedingt weiterzuentwickeln. 
 
 ÜBERFACHLICHE KOMPETENZ : 
 
 Die Studierenden vertiefen ihre Kenntnis in der strukturierten Herangehensweise an Problemstellungen 
 Die Studierenden sammeln Erfahrung bei der Zusammenarbeit im Team (Teamfähigkeit)', 'Die Themen der Lehrveranstaltungen sollen so gewählt werden, dass ein effektives Arbeiten und die termingerechte Zielerreichung möglich ist. Eine Beteiligung an Projekten der betrieblichen Praxis wird angestrebt. 
 Aspekte der Projektabwicklung, beispielsweise: Anforderungsdefinition, Einrichten einer Projekt-Infrastruktur, Konfigurationsmanagement, Change-Management, Entwicklung, Qualitätssicherung, Abnahme der Projektergebnisse. 
 Aktuelle Methoden der agilen Softwareentwicklung wie SCRUM sollen angewendet werden.', 'Tafel und Folien (Powerpoint), multimediale Präsentationen, groupware, Sitzungen, projektspezifische Arbeitsleistung, Fallstudien', 'Balzert H.: Lehrbuch der Software-Technik: Softwaremanagement, Springer 
 Burghardt M.: Projektmanagement, Publicis 
 DeMarco T., Lister T., Hruschka P.: Wien wartet auf dich, Hanser 
 DeMarco T.: Der Termin, Hanser, München, 2007 
 Kellner H.: Die Kunst IT-Projekte zum Erfolg zu führen, Hanser 
 Süß G., Ehrl-Gruber B.: Praxishandbuch Projektmanagement, WEKA 
 Tiemeyer E. (Hrsg.): Handbuch IT-Projektmanagement: Vorgehensmodelle, Managementinstrumente, Good Practices , Hanser Verlag 
 Integriertes Qualitätsmanagement und Leistungserbringungsmanagement mit Governance, Risk und Compliance (GRC): Standard für ein compliance-, risiko-, chancen- und wertorientiertes, integriertes Qualitäts- und Leistungserbringungsmanagement (mit Entwicklung, Einkauf und Marketing/Vertrieb) : Anforderungen an die Führungskräfte von heute und morgen, GMRC-Verlag');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 187L, 'Projektstudium: Didaktik der Informatik', 'https://zpa.cs.hm.edu/public/module/187/', 'Prof. Dr. Gudrun Socher', '4', '5', 'Deutsch, Englisch', 2, 'Praktikum', 'nach Ankündigung', '60 Präsenzstunden, 90 Stunden Eigen- und Gruppenarbeit', 'Voraussetzung ist der Erwerb von 60 ECTS-Kreditpunkten aus den ersten vier Studiensemestern', 'Die Studierenden sind in der Lage, 
 
 ihre fachlichen Kompetenzen weiterzuentwickeln nach dem Prinzip „Lernen durch Lehren“. 
 fachliche Inhalte verständlich aufzubereiten.  
 ihre Methodenkompetenzen weiterzuentwickeln. 
 ihre eigenen sozialen Kompetenzen weiterzuentwickeln.  
 
 Die erarbeiteten Materialien und Konzepte sollen nach Möglichkeit unmittelbar in der Praxis erprobt werden, etwa im Rahmen einer Schulkooperation oder in einer Grundlagen-Lehrveranstaltung.', 'Die Studierenden gestalten selbständig Lerneinheiten und entwickeln die dazu gehörenden Materialien. Die Lerneinheiten sollen aktivierende Lernformate beinhalten, die die Zielgruppe dazu anregt, sich selbst aktiv mit dem Stoff auseinander zu setzen. Beispiele sind Just-in-Time-Teaching, Peer Instruction, Problem-Based Learning, Lernspiele oder auch interaktive, multimediale Anschauungsobjekte. 
 Die Zielgruppe, die in den Lerneinheiten adressiert werden soll, wird jeweils in der Veranstaltung vorgegeben. Beispiele sind Studierende z.B. der Informatik bzw. Wirtschaftsinformatik im 1. und 2. Semester oder auch Schülerinnen und Schüler der Mittelstufe einer Realschule oder eines Gymnasiums.Für alle Lernmaterialien ist eine zielgruppenorientierte Präsentation zur nachhaltigen Vermittlung von Lerninhalten zu wählen.', 'Tafel, Beamer, Demonstrationen, Versuche', 'Humbert: Didaktik der Informatik (ISBN 3-8351-0038-6);
Gallenbacher: Abenteuer Informatik (ISBN 9-783827-416353);');
INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 188L, 'Projektstudium (IF)', 'https://zpa.cs.hm.edu/public/module/188/', 'Prof. Dr. Axel Böttcher', '8', '10', 'Deutsch, Englisch', 2, 'Praktikum', 'im Wechsel mit anderen Fächern der gleichen Fachgruppe', '120 Präsenzstunden, 150 Stunden Erstellen der Projektarbeit, 30 Stunden Vorbereitung des eigenen Vortrags.', 'Grundlegende Kenntnisse der Informatik nach dem 5. Studiensemester.', 'Lernziele:  Ziel des Moduls ist das Trainieren des Einsatzes von Arbeitspraktiken und Techniken der Projektabwicklung sowie des situativen Lernens anhand eines konkreten, komplexeren Projektes aus dem Bereich Informatik unter realitätsnahen Bedingungen. 
 Kompetenzen:  Fähigkeit komplexe technische Problemstellungen zu analysieren, Lösungsansätze zu entwerfen, in Teamarbeit zu implementieren, zu testen und das Ergebnis zu präsentieren.', 'Die Themen der Lehrveranstaltungen sollen so gewählt werden, dass ein effektives Arbeiten und die termingerechte Zielerreichung möglich ist. Eine Beteiligung an Projekten der betrieblichen Praxis wird angestrebt. 
 Aspekte der Projektentwicklung, beispielsweise: Anforderungsdefinition, Einrichten einer Projekt-Infrastruktur, Konfigurationsmanagement, Change-Management, Entwicklung. Qualitätssicherung, Abnahme der Projektergebnisse. 
 In Referaten stellen die Teilnehmer den Entwicklungsprozess, sowie Soft- und Hardware-komponenten des zu entwickelnden Systems vor, wie beispielsweise: 
 
