INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 214L, 'Sicherheit von Web-Anwendungen', 'https://zpa.cs.hm.edu/public/module/214/', 'Prof. Dr. Alf Zugenmaier', '4', '5', 'Deutsch', 1, 'SU mit Praktikum', 'nach Ankündigung', 'Präsenzstudium: ca. 42 Std., Eigenstudium: ca. 108 Std.', 'Grundlagen zu IT-Sicherheit, z.B. erworben durch Vorlesung IT-Sicherheit, grundlegende Kenntnisse von IT-Systemen (Netzwerk, Betriebssysteme), grundlegende Programmierkenntnisse', 'Lernziele:  Ziel des Moduls ist die Vermittlung von Kenntnissen betreffend die Planung, die Realisierung und das Testen von sicheren Web-Anwendungen. 
 Fach- und Methoden-Kompetenzen:   
 
