INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 266L, 'Wirtschaftsmathematik II', 'https://zpa.cs.hm.edu/public/module/266/', 'Prof. Dr. Silja Grawert', '4', '5', 'Deutsch', 1, 'SU mit Übung', 'in jedem Sommersemester', 'Präsenzstudium: ca. 42 Std., Eigenstudium: ca. 108 Std.', 'Inhalte aus dem Modul Wirtschaftsmathematik I (B01)', 'Lernziele:  Die Studierenden sollen weiterführende Kenntnisse essentieller mathematischer Grundlagen und Modellierungsmethoden für Anwendungen in Informatik und Wirtschaft erwerben. Sie beherrschen wichtige finanzmathematische Konzepte (z.B. Äquivalenzprinzip), können mehrdimensionale Ableitungen bilden und Extremwerte von mehrdimensionalen Funktionen ermitteln.     
 Fach- und Methodenkompetenz:  Fähigkeit zur praxisorientierten Anwendung ausgewählter Spezialthemen der Wirtschaftsmathematik. Die im ersten Semester erlernten Fähigkeiten werden ins Mehrdimensionale verallgemeinert und insbesondere in der Finanzmathematik eingesetzt.  
