INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 299L, 'Seminar Informatik', 'https://zpa.cs.hm.edu/public/module/299/', 'Prof. Dr. Axel Böttcher', '4', '5', 'Deutsch, Englisch', 2, 'Seminar', '-', '45 Präsenzstunden, 50 Stunden Vorbereitung des eigenen Vortrags, 55 Stunden Erstellen der Seminararbeit', 'Formal: keine 
Inhaltlich: Module der ersten drei Semester 
Weitere Voraussetzung: Teilnahmenachweis, d.h. Anwesenheitspflicht.', 'In diesem Modul werden, je nach Dozierendem, ein oder mehrere spezielle Themen der Informatik vertieft. Dabei erwerben die Studierenden grundlegende Kompetenzen zum wissenschaftlichen Arbeiten. 
 Die Studierenden ... 
 
