INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 300L, 'Kotlin - Eine pragmatische JVM-Sprache', 'https://zpa.cs.hm.edu/public/module/300/', 'Prof. Dr. Reinhard Schiedermeier', '4', '5', 'Deutsch, Englisch', 2, 'Seminar', 'nach Ankündigung', '60 Präsenzstunden, 50 Stunden Vorbereitung des eigenen Vortrags, 40 Stunden Erstellen der Seminararbeit', 'Softwareentwicklung I (IF-I-B-104) und II (IF-I-B-204)', 'Die Teilnehmer kennen die Merkmale und Grenzen der Programmiersprache Kotlin und können sie gegenüber den Eigenschaften anderer Sprachen einordnen. Sie sind in der Lage, die Eigenschaften der Sprache zur Entwicklung neuer und zur Weiterent­wicklung bestehender Software zu nutzen.', 'Kotlin ist eine Programmiersprache, die der Compiler in Java-Bytecode oder Javascript übersetzt. Diese LVA befasst sich mit den Merkmalen von Kotlin und deren Anwendung zur Lösung konkreter Probleme. Sie greift auch die Arbeitsweise des Laufzeitsystems und ausgewählter Entwicklungswerkzeuge auf. Themen sind unter anderem:
 * Variablen und Werte
