INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 342L, 'IT-Sicherheit und technischer Datenschutz', 'https://zpa.cs.hm.edu/public/module/342/', 'Prof. Dr. Peter Trapp', '4', '5', 'Deutsch', 1, 'SU mit Praktikum', 'in jedem Sommersemester', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'keine', 'Fachkompetenzen  Nach der Teilnahme an den Modulveranstaltungen sind die Studierenden in der Lage verschiedene Aufgabenstellungen, Prinzipien, Konzepte und Mechanismen zur Realisierung von IT-Sicherheit zu beurteilen und zu analysieren. Weiterhin werden Maßnahmen zum technischen Schutz von privaten Daten (technischer Datenschutz) vermittelt. Diese gilt es zu verstehen und anzuwenden. 
