INSERT INTO module (id, zpa_id, name, url, verantwortlich, sws, ects, sprachen, anzahl_sprachen, lehrform, angebot, aufwand, voraussetzungen, ziele, inhalt, medien_und_methoden, literatur) VALUES (default, 361L, 'Cyber Defense', 'https://zpa.cs.hm.edu/public/module/361/', 'Prof. Dr.-Ing. Thomas Schreck', '4', '5', 'Deutsch', 1, 'SU mit Praktikum', 'im Wechsel mit anderen Fächern der gleichen Fachgruppe', '30 Präsenzstunden Vorlesung, 30 Präsenzstunden Praktikum, 45 Stunden Vor-/Nachbereitung des Praktikums, 45 Stunden Nachbereitung der Vorlesung und Prüfungsvorbereitung', 'Grundlagenkenntnisse der IT-Sicherheit, Wissen über Netzwerke und Betriebssysteme', 'Folgende Kompetenzen werden vermittelt: 
 
